mapper.select:
select
  id, parent_id, name, type, path, component, perms, icon, sort_value, status, create_time, update_time, is_deleted
from sys_menu


        and id = #{id}


        and parent_id = #{parentId}


        and name = #{name}


        and type = #{type}


        and path = #{path}


        and component = #{component}


        and perms = #{perms}


        and icon = #{icon}


        and sort_value = #{sortValue}


        and status = #{status}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from sys_menu


        and id = #{id}


        and parent_id = #{parentId}


        and name = #{name}


        and type = #{type}


        and path = #{path}


        and component = #{component}


        and perms = #{perms}


        and icon = #{icon}


        and sort_value = #{sortValue}


        and status = #{status}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}
